import React from 'react';
import logo from './logo.svg';
import './App.css';
import Movie from './Movie'


class App extends React.Component {

  state = {
    greeting: 'Hello!',
    movies: [
      {
        title: "치즈태비는 귀여워",
        poster: "http://www.catcare.or.kr/files/attach/images/260/993/031/003/6f05704b01915d691d67e26043db9997.jpg"
      },

      {
        title: "고양이 귀여워",
        poster: "http://www.catcare.or.kr/files/attach/images/260/993/031/003/2dd7ec60f813312f5073d261b7b36396.jpg", 
      },

      {
        title: "노랑노랑",
        poster: "http://www.catcare.or.kr/files/attach/images/260/993/031/003/d66fdc9377cd6abc2064d232a666f5b3.jpg"
      },

      {
        title: "애깅이",
        poster: "http://www.catcare.or.kr/files/attach/images/260/361/031/003/e8249188d0d149eb93a6682035bcf797.jpg"
      }
    ]
  }

  componentDidMount(){
    setTimeout(function() {
      console.log('hello')
    }, 1000) 

    setTimeout(() => {
      this.setState({

        movies: [
          ...this.state.movies,
          {
            title: "치즈태비 짱짱",
            poster: "http://m1.daumcdn.net/cfile220/R400x0/2326BC4F573D5BAC345B36"
          }
        ]
      })

      setTimeout(()=> {
        this.setState({
          movies: [
            ...this.state.movies,
            {
              title: "귀여웡",
              poster: "http://m1.daumcdn.net/cfile223/R400x0/252F564F573D5BB12D94CA"
            }
          ]
        })
      }, 1000)


    }, 1000)
  }


  render() {
    return (
      <div className="App">
        {this.state.movies.map((movie, index) => {
          return <Movie title={movie.title} poster={movie.poster} key={index}/>
        })}
      </div>
    );
  }
} 

export default App;
