import React, { Component } from 'react';
import './Movie.css';
import PropTypes from 'prop-types';

class Movie extends Component{

    static propTypes = {
        title: PropTypes.string.isRequired,
        poster: PropTypes.string
    }
    // 예상치 못한 값이어도(string이 아니라 int여도)
    // 화면에 표시는 되지만, warning 메시지가 뜬다

    render() {
        const { title, poster } = this.props
        return (
            <div>
                <MoviePoster poster={poster} />
                {/* 이렇게 넘겨준 이유는, props는 Movie에게 넘어온 것이지 MoviePoster에게 넘어온 것이 아니기 때문에 */}
                {/* MoviePoster에게도 props를 전달해주려면 별도로 전달을 해줘야 한다 */}

                <h1>{title}</h1>
            </div>
        )
    }
}


function MoviePoster({poster}) {
    return (
        <img src={poster} alt="고양이는최고양" /> 
        // class가 아니라서 this.props를 쓰지 않는다는건 무슨말
        // 함수형 컴포넌트니까, 파라미터로 받으니까
    )
}



export default Movie;
