import React from 'react';
import logo from './logo.svg';
import './App.css';
import Movie from './Movie'

const movieTitles=[
  "치즈태비는 귀여워",
  "고양이 귀여워",
  "노랑노랑",
  "애깅이"
]

const movieImages=[
  "http://www.catcare.or.kr/files/attach/images/260/993/031/003/6f05704b01915d691d67e26043db9997.jpg",
  "http://www.catcare.or.kr/files/attach/images/260/993/031/003/2dd7ec60f813312f5073d261b7b36396.jpg",
  "http://www.catcare.or.kr/files/attach/images/260/993/031/003/d66fdc9377cd6abc2064d232a666f5b3.jpg",
  "http://www.catcare.or.kr/files/attach/images/260/361/031/003/e8249188d0d149eb93a6682035bcf797.jpg"
]

const movies = [
  {
    title: "치즈태비는 귀여워",
    poster: "http://www.catcare.or.kr/files/attach/images/260/993/031/003/6f05704b01915d691d67e26043db9997.jpg"
  },

  {
    title: "고양이 귀여워",
    poster: "http://www.catcare.or.kr/files/attach/images/260/993/031/003/2dd7ec60f813312f5073d261b7b36396.jpg", 
  },

  {
    title: "노랑노랑",
    poster: "http://www.catcare.or.kr/files/attach/images/260/993/031/003/d66fdc9377cd6abc2064d232a666f5b3.jpg"
  },

  {
    title: "애깅이",
    poster: "http://www.catcare.or.kr/files/attach/images/260/361/031/003/e8249188d0d149eb93a6682035bcf797.jpg"
  }
]

class App extends React.Component {

  state = {
    greeting: 'Hello!'
  }

  componentDidMount(){
    setTimeout(() => {
      this.setState({
        greeting: 'Hello again!'
      })
    }, 2000)

    setTimeout(() => {
      this.setState({
        greeting: 'Hi!'
      })
    }, 4000)
  }

  render() {
    return (
      <div className="App">
        {this.state.greeting}
        {movies.map((movie, index) => {
          return <Movie title={movie.title} poster={movie.poster} key={index}/>
        })}
      </div>
    );
  }
} 

export default App;
