import React from 'react';
import logo from './logo.svg';
import './App.css';
import Movie from './Movie'


class App extends React.Component {

  state = {}

  componentDidMount(){
    fetch('https://yts.am/api/v2/list_movies.json?quality=3D?sort_by=rating')
    .then()
    .catch(err => console.log(err))

  }

  _renderMovies = () => {
    const movies = this.state.movies.map((movie, index) => {
      return <Movie title={movie.title} poster={movie.poster} key={index} ></Movie>
    })

    return movies
  }

  _renderLoading = () => {
    const url = "https://loading.io/spinners/rolling/index.curve-bars-loading-indicator.gif"
    return <img src={url}></img>
  }

  render() {
    return (
      <div className="App">
        {this.state.movies ? this._renderMovies() : this._renderLoading() }
      </div>
    );
  }
} 

export default App;
